package client;import java.util.Random;public class Client {    static private final int countOfRequest = 10;    static private int countOfResponse = 0;    public static void main(String[] args) {        try {            ClientSession clientSession = ClientSession.createSession();            for (int i = 0; i < countOfRequest; i++) {                String command = generateRandomString(i);                System.out.println("Command: " + command);                clientSession.sendMessage(command);            }            clientSession.sendMessage("");            while (countOfResponse != countOfRequest) {                System.out.println("Answer by server: " + clientSession.getMessage());                countOfResponse++;            }            clientSession.closeSession();            System.out.println("Client was closed.");        } catch (Exception e) {            System.err.println(e.getMessage());        }    }    public static String generateRandomString(int i) {        Random random = new Random();        StringBuilder sb = new StringBuilder();        for (int j = 0; j < i + 1; j++) {            sb.append(Character.toChars(random.nextInt(97, 122)));        }        return sb.toString();    }}